'use client'

import { useState, useEffect } from 'react'
import { Tabs, TabsContent, TabsList, TabsTrigger } from "./ui/tabs"
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "./ui/card"
import { Button } from "./ui/button"
import { Package, Heart, LogOut, ShoppingCart } from 'lucide-react'
import { Badge } from "./ui/badge"
import Navbar from './Navbar'

interface Order {
  orderId: string;
  status: string;
  totalPrice: number;
  items: {
    bookTitle: string;
    quantity: number;
  }[];
}

interface WishlistItem {
  id: string;
  title: string;
  author: string;
  price: number;
  image: string;
}

export default function ProfilePage() {
  const [orders, setOrders] = useState<Order[]>([]);
  const [wishlist, setWishlist] = useState<WishlistItem[]>([]);
  const [loadingOrders, setLoadingOrders] = useState(true);
  const [loadingWishlist, setLoadingWishlist] = useState(true);
  const [fetchError, setFetchError] = useState<{ orders: string | null; wishlist: string | null }>({
    orders: null,
    wishlist: null,
  });
  const [isLoadingCart, setIsLoadingCart] = useState(false);

  const fetchOrders = async (token: string) => {
    try {
      const response = await fetch('http://localhost:5000/api/orders/user-orders', {
        headers: { Authorization: `Bearer ${token}` },
      });

      if (!response.ok) {
        throw new Error(`Failed to fetch orders: ${response.status}`);
      }

      const data: Order[] = await response.json();
      setOrders(data);
    } catch (error) {
      const err = error as Error;
      setFetchError((prev) => ({ ...prev, orders: err.message || "An unknown error occurred." }));
    } finally {
      setLoadingOrders(false);
    }
  };

  const fetchWishlist = async (token: string) => {
    try {
      const response = await fetch('http://localhost:5000/api/wishlist', {
        headers: { Authorization: `Bearer ${token}` },
      });

      if (!response.ok) {
        throw new Error(`Failed to fetch wishlist: ${response.status}`);
      }

      const data = await response.json();
      const formattedWishlist = data.books.map((book: { _id: string; title: string; author: string; price: number; image: string }) => ({
        id: book._id,
        title: book.title,
        author: book.author,
        price: book.price,
        image: book.image,
      }));
      setWishlist(formattedWishlist);
    } catch (error) {
      const err = error as Error;
      setFetchError((prev) => ({ ...prev, wishlist: err.message || "An unknown error occurred." }));
    } finally {
      setLoadingWishlist(false);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    window.location.href = '/'; // Redirect to home page
  };

  const addToCart = async (bookId: string) => {
    setIsLoadingCart(true);
    const token = localStorage.getItem('token');
    if (!token) {
      alert('Please log in first!');
      setIsLoadingCart(false);
      return;
    }

    try {
      const response = await fetch('http://localhost:5000/api/carts/add', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`,
        },
        body: JSON.stringify({ bookId, quantity: 1 }),
      });

      if (response.ok) {
        console.log('Book added to cart');
        alert('Book added to cart successfully!');
      } else {
        const data = await response.json();
        console.error('Error response:', data);
        alert(data.message || 'Error adding book to cart.');
      }
    } catch (err) {
      console.error('Error:', err);
      alert('An error occurred while adding the book to the cart. Please try again.');
    } finally {
      setIsLoadingCart(false);
    }
  };

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      fetchOrders(token);
      fetchWishlist(token);
    }
  }, []);

  const removeFromWishlist = async (id: string) => {
    const token = localStorage.getItem('token');
    if (!token) {
      alert('Please log in first!');
      return;
    }

    try {
      const response = await fetch(`http://localhost:5000/api/wishlist/remove/${id}`, {
        method: 'DELETE',
        headers: {
          'Authorization': `Bearer ${token}`,
        },
      });

      if (response.ok) {
        setWishlist(wishlist.filter(item => item.id !== id));
        alert('Book removed from wishlist successfully!');
      } else {
        const data = await response.json();
        alert(data.message || 'Error removing book from wishlist.');
      }
    } catch (err) {
      console.error('Error:', err);
      alert('An error occurred while removing the book from the wishlist. Please try again.');
    }
  };

  return (
    <div className="min-h-screen bg-gray-100">
      <Navbar />
      <div className="container mx-auto px-4 py-8">
        <h1 className="text-3xl font-bold mb-8">My Profile</h1>
        <Tabs defaultValue="orders">
          <TabsList className="grid w-full grid-cols-3 gap-4 mb-8">
            <TabsTrigger value="orders" className="group flex items-center justify-center p-4 rounded-lg bg-white shadow hover:shadow-lg hover:bg-gray-50 transition">
              <Package className="mr-2 h-5 w-5 text-gray-700 group-hover:text-gray-900" />
              <span className="text-gray-700 group-hover:text-gray-900 font-medium">Orders</span>
            </TabsTrigger>
            <TabsTrigger value="wishlist" className="group flex items-center justify-center p-4 rounded-lg bg-white shadow hover:shadow-lg hover:bg-gray-50 transition">
              <Heart className="mr-2 h-5 w-5 text-gray-700 group-hover:text-gray-900" />
              <span className="text-gray-700 group-hover:text-gray-900 font-medium">Wishlist</span>
            </TabsTrigger>
            <TabsTrigger value="logout" className="group flex items-center justify-center p-4 rounded-lg bg-white shadow hover:shadow-lg hover:bg-gray-50 transition">
              <LogOut className="mr-2 h-5 w-5 text-gray-700 group-hover:text-gray-900" />
              <span className="text-gray-700 group-hover:text-gray-900 font-medium">Logout</span>
            </TabsTrigger>
          </TabsList>

          <TabsContent value="orders">
            <Card>
              <CardHeader>
                <CardTitle>Your Orders</CardTitle>
                <CardDescription>View and manage your order history.</CardDescription>
              </CardHeader>
              <CardContent>
                {loadingOrders ? (
                  <p>Loading orders...</p>
                ) : fetchError.orders ? (
                  <p className="text-red-500">Error: {fetchError.orders}</p>
                ) : orders.length > 0 ? (
                  <div className="space-y-6">
                    {orders.map((order) => (
                      <div key={order.orderId} className="p-4 border rounded-lg shadow-sm bg-white">
                        <div className="flex justify-between items-center">
                          <h3 className="text-lg font-semibold">Order #{order.orderId}</h3>
                          <Badge>{order.status}</Badge>
                        </div>
                        <ul className="mt-2 space-y-1">
                          {order.items.map((item, index) => (
                            <li key={index} className="flex justify-between">
                              <span>{item.bookTitle} (x{item.quantity})</span>
                            </li>
                          ))}
                        </ul>
                        <div className="mt-4 text-right font-semibold">Total: ${order.totalPrice.toFixed(2)}</div>
                      </div>
                    ))}
                  </div>
                ) : (
                  <p>No orders found.</p>
                )}
              </CardContent>
            </Card>
          </TabsContent>

          <TabsContent value="wishlist">
            <Card>
              <CardHeader>
                <CardTitle>Your Wishlist</CardTitle>
                <CardDescription>Books you've saved for later.</CardDescription>
              </CardHeader>
              <CardContent>
                {loadingWishlist ? (
                  <p>Loading wishlist...</p>
                ) : fetchError.wishlist ? (
                  <p className="text-red-500">Error: {fetchError.wishlist}</p>
                ) : wishlist.length > 0 ? (
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    {wishlist.map((item) => (
                      <div key={item.id} className="flex p-4 border rounded-lg shadow-sm bg-white">
                        <img src={item.image || '/placeholder.svg'} alt={item.title} className="w-24 h-32 object-cover rounded-md" />
                        <div className="ml-4 flex-1">
                          <h3 className="font-semibold">{item.title}</h3>
                          <p className="text-sm text-gray-500">{item.author}</p>
                          <p className="text-lg font-bold mt-2">${item.price.toFixed(2)}</p>
                          <div className="flex justify-between mt-4">
                            <Button size="sm" className="flex items-center" onClick={() => addToCart(item.id)}>
                              <ShoppingCart className="mr-2 h-4 w-4" />
                              Add to Cart
                            </Button>
                            <Button variant="ghost" size="sm" onClick={() => removeFromWishlist(item.id)} className="text-red-500">
                              Remove
                            </Button>
                          </div>
                        </div>
                      </div>
                    ))}
                  </div>
                ) : (
                  <p>Your wishlist is empty.</p>
                )}
              </CardContent>
            </Card>
          </TabsContent>

          <TabsContent value="logout">
            <Card>
              <CardHeader>
                <CardTitle>Logout</CardTitle>
                <CardDescription>Are you sure you want to log out?</CardDescription>
              </CardHeader>
              <CardContent>
                <Dialog>
                  <DialogTrigger>
                    <Button className="w-full">Logout</Button>
                  </DialogTrigger>
                  <DialogContent>
                    <p>Are you sure you want to log out?</p>
                    <DialogFooter>
                      <Button variant="ghost" onClick={() => console.log('Cancel')}>Cancel</Button>
                      <Button onClick={handleLogout}>Confirm Logout</Button>
                    </DialogFooter>
                  </DialogContent>
                </Dialog>
              </CardContent>
            </Card>
          </TabsContent>
        </Tabs>
      </div>
    </div>
  );
}
