'use client'

import { useState, useEffect } from 'react'
import { Tabs, TabsContent, TabsList, TabsTrigger } from "./ui/tabs"
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "./ui/card"
import { Button } from "./ui/button"
import { Package, Heart, LogOut, ShoppingCart } from 'lucide-react'
import { Badge } from "./ui/badge"
import Navbar from './Navbar'

interface Order {
  orderId: string;
  status: string;
  totalPrice: number;
  items: {
    bookTitle: string;
    quantity: number;
  }[];
}

interface WishlistItem {
  id: string;
  title: string;
  author: string;
  price: number;
  image: string;
}

export default function ProfilePage() {
  const [orders, setOrders] = useState<Order[]>([]);
  const [wishlist, setWishlist] = useState<WishlistItem[]>([]);
  const [loadingOrders, setLoadingOrders] = useState(true);
  const [loadingWishlist, setLoadingWishlist] = useState(true);
  const [fetchError, setFetchError] = useState<{ orders: string | null; wishlist: string | null }>({
    orders: null,
    wishlist: null,
  });

  const fetchOrders = async (token: string) => {
    try {
      const response = await fetch('http://localhost:5000/api/orders/user-orders', {
        headers: { Authorization: `Bearer ${token}` },
      });

      if (!response.ok) {
        throw new Error(`Failed to fetch orders: ${response.status}`);
      }

      const data: Order[] = await response.json();
      setOrders(data);
    } catch (error) {
      const err = error as Error;
      setFetchError((prev) => ({ ...prev, orders: err.message || "An unknown error occurred." }));
    } finally {
      setLoadingOrders(false);
    }
  };

  const fetchWishlist = async (token: string) => {
    try {
      const response = await fetch('http://localhost:5000/api/wishlist', {
        headers: { Authorization: `Bearer ${token}` },
      });

      if (!response.ok) {
        throw new Error(`Failed to fetch wishlist: ${response.status}`);
      }

      const data = await response.json();
      const formattedWishlist = data.books.map((book: { _id: string; title: string; author: string; price: number; image: string }) => ({
        id: book._id,
        title: book.title,
        author: book.author,
        price: book.price,
        image: book.image,
      }));
      setWishlist(formattedWishlist);
    } catch (error) {
      const err = error as Error;
      setFetchError((prev) => ({ ...prev, wishlist: err.message || "An unknown error occurred." }));
    } finally {
      setLoadingWishlist(false);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    window.location.href = '/'; // Redirect to home page
  };

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      fetchOrders(token);
      fetchWishlist(token);
    }
  }, []);

  const removeFromWishlist = (id: string) => {
    setWishlist(wishlist.filter(item => item.id !== id));
  };

  return (
    <div>
      <Navbar /> {/* Add Navbar at the top */}
      <div className="container mx-auto px-4 py-8">
        <h1 className="text-3xl font-bold mb-6">My Profile</h1>
        <Tabs defaultValue="orders">
          <TabsList className="grid w-full grid-cols-3 mb-8 shadow-md rounded-lg bg-white">
            <TabsTrigger value="orders" className="flex items-center justify-center py-3 px-4 rounded-tl-lg transition-all hover:bg-gray-100 active:bg-gray-200 focus:ring-2 focus:ring-primary">
              <Package className="mr-2 h-4 w-4" />
              Orders
            </TabsTrigger>
            <TabsTrigger value="wishlist" className="flex items-center justify-center py-3 px-4 transition-all hover:bg-gray-100 active:bg-gray-200 focus:ring-2 focus:ring-primary">
              <Heart className="mr-2 h-4 w-4" />
              Wishlist
            </TabsTrigger>
            <TabsTrigger value="logout" className="flex items-center justify-center py-3 px-4 rounded-tr-lg transition-all hover:bg-gray-100 active:bg-gray-200 focus:ring-2 focus:ring-primary">
              <LogOut className="mr-2 h-4 w-4" />
              Logout
            </TabsTrigger>
          </TabsList>

          <TabsContent value="orders">
            <Card>
              <CardHeader>
                <CardTitle>Your Orders</CardTitle>
                <CardDescription>View and manage your order history.</CardDescription>
              </CardHeader>
              <CardContent>
                {loadingOrders ? (
                  <p>Loading orders...</p>
                ) : fetchError.orders ? (
                  <p className="text-red-500">Error: {fetchError.orders}</p>
                ) : orders.length > 0 ? (
                  orders.map(order => (
                    <div key={order.orderId} className="mb-6 p-4 border rounded-lg">
                      <div className="flex justify-between items-center mb-2">
                        <h3 className="text-lg font-semibold">Order #{order.orderId}</h3>
                        <Badge>{order.status}</Badge>
                      </div>
                      <ul className="space-y-2 mb-2">
                        {order.items.map((item, index) => (
                          <li key={index} className="flex justify-between">
                            <span>{item.bookTitle} (x{item.quantity})</span>
                          </li>
                        ))}
                      </ul>
                      <div className="text-right font-semibold">
                        Total: ${order.totalPrice.toFixed(2)}
                      </div>
                    </div>
                  ))
                ) : (
                  <p>No orders found.</p>
                )}
              </CardContent>
            </Card>
          </TabsContent>

          <TabsContent value="wishlist">
            <Card>
              <CardHeader>
                <CardTitle>Your Wishlist</CardTitle>
                <CardDescription>Books you've saved for later.</CardDescription>
              </CardHeader>
              <CardContent>
                {loadingWishlist ? (
                  <p>Loading wishlist...</p>
                ) : fetchError.wishlist ? (
                  <p className="text-red-500">Error: {fetchError.wishlist}</p>
                ) : wishlist.length > 0 ? (
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    {wishlist.map(item => (
                      <div key={item.id} className="flex border rounded-lg overflow-hidden">
                        <img src={item.image} alt={item.title} className="w-24 h-32 object-cover" />
                        <div className="flex-1 p-4 flex flex-col justify-between">
                          <div>
                            <h3 className="font-semibold">{item.title}</h3>
                            <p className="text-sm text-gray-500">{item.author}</p>
                            <p className="text-lg font-bold mt-2">${item.price.toFixed(2)}</p>
                          </div>
                          <div className="flex justify-between items-center mt-2">
                            <Button size="sm" className="flex items-center">
                              <ShoppingCart className="mr-2 h-4 w-4" />
                              Add to Cart
                            </Button>
                            <Button variant="ghost" size="sm" onClick={() => removeFromWishlist(item.id)}>
                              Remove
                            </Button>
                          </div>
                        </div>
                      </div>
                    ))}
                  </div>
                ) : (
                  <p>Your wishlist is empty.</p>
                )}
              </CardContent>
            </Card>
          </TabsContent>

          <TabsContent value="logout">
            <Card>
              <CardHeader>
                <CardTitle>Logout</CardTitle>
                <CardDescription>Are you sure you want to log out?</CardDescription>
              </CardHeader>
              <CardContent>
                <Button onClick={handleLogout}>Confirm Logout</Button>
              </CardContent>
            </Card>
          </TabsContent>
        </Tabs>
      </div>
    </div>
  );
}
