'use client'
import { useState, useEffect } from 'react'
import { Heart, Search, ShoppingCart, User } from 'lucide-react'
import { Link } from 'react-router-dom';
import { Button } from "./ui/button"
import { Input } from "./ui/input"
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "./ui/select"
import { Card, CardContent, CardFooter, CardHeader, CardTitle } from "./ui/card"
import { Badge } from "./ui/badge"
import Navbar from './Navbar'

type Book = {
  _id: string;
  title: string;
  author: string;
  price: number;
  discount: number;
  category: string;
  images: string[];
}

export default function HomePage() {
  const [books, setBooks] = useState<Book[]>([]);
  const [categories, setCategories] = useState<string[]>([]);
  const [authors, setAuthors] = useState<string[]>([]);
  const [selectedCategory, setSelectedCategory] = useState("All");
  const [selectedAuthor, setSelectedAuthor] = useState("All");
  const [wishlist, setWishlist] = useState<string[]>([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  useEffect(() => {
    fetchBooks();
    checkAuthStatus();
  }, []);

  useEffect(() => {
    if (isAuthenticated) {
      fetchWishlist();
    }
  }, [isAuthenticated]);

  const checkAuthStatus = () => {
    const token = localStorage.getItem('token');
    setIsAuthenticated(!!token);
  };

  const fetchBooks = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/books');
      const data: Book[] = await response.json();
      setBooks(data);
  
      const uniqueCategories: string[] = Array.from(new Set(data.map((book: Book) => book.category)));
      const uniqueAuthors: string[] = Array.from(new Set(data.map((book: Book) => book.author)));
  
      setCategories(['All', ...uniqueCategories]);
      setAuthors(['All', ...uniqueAuthors]);
    } catch (error) {
      console.error('Error fetching books:', error);
    }
  };

  const fetchWishlist = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:5000/api/wishlist', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      if (response.status === 401) {
        setIsAuthenticated(false);
        return;
      }
      const data = await response.json();
      setWishlist(data.books.map((book: Book) => book._id));
    } catch (error) {
      console.error('Error fetching wishlist:', error);
    }
  };

  const toggleWishlist = async (bookId: string) => {
    if (!isAuthenticated) {
      alert('Please log in to add items to your wishlist');
      return;
    }

    try {
      const token = localStorage.getItem('token');
      if (wishlist.includes(bookId)) {
        await fetch(`http://localhost:5000/api/wishlist/remove/${bookId}`, {
          method: 'DELETE',
          headers: {
            'Authorization': `Bearer ${token}`
          }
        });
        setWishlist(wishlist.filter(id => id !== bookId));
      } else {
        await fetch('http://localhost:5000/api/wishlist/add', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${token}`
          },
          body: JSON.stringify({ bookId })
        });
        setWishlist([...wishlist, bookId]);
      }
    } catch (error) {
      console.error('Error updating wishlist:', error);
    }
  };

  const addToCart = async (bookId: string) => {
    if (!isAuthenticated) {
      alert('Please log in to add items to your cart');
      return;
    }

    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:5000/api/carts/add', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify({ bookId, quantity: 1 })
      });

      if (response.ok) {
        alert('Book added to cart successfully');
      } else {
        alert('Failed to add book to cart');
      }
    } catch (error) {
      console.error('Error adding to cart:', error);
      alert('Error adding to cart');
    }
  };

  const filteredBooks = books.filter(book =>
    (selectedCategory === "All" || book.category === selectedCategory) &&
    (selectedAuthor === "All" || book.author === selectedAuthor) &&
    (book.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
      book.author.toLowerCase().includes(searchTerm.toLowerCase()))
  );

  const discountedBooks = filteredBooks.filter(book => book.discount > 0);

  return (
    <div className="min-h-screen bg-gray-50">
      <Navbar />
      <main className="container mx-auto px-4 py-8">
        {isAuthenticated ? (
          <>
            <section className="mb-8">
              <div className="flex items-center space-x-4 mb-6">
                <Input
                  type="text"
                  placeholder="Search for books..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  className="w-full md:w-1/2"
                />
                <div className="w-48">
                  <Select value={selectedCategory} onValueChange={setSelectedCategory}>
                    <SelectTrigger>
                      <SelectValue placeholder="Select Category" />
                    </SelectTrigger>
                    <SelectContent>
                      {categories.map((category) => (
                        <SelectItem key={category} value={category}>
                          {category}
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                </div>

                <div className="w-48">
                  <Select value={selectedAuthor} onValueChange={setSelectedAuthor}>
                    <SelectTrigger>
                      <SelectValue placeholder="Select Author" />
                    </SelectTrigger>
                    <SelectContent>
                      {authors.map((author) => (
                        <SelectItem key={author} value={author}>
                          {author}
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                </div>
              </div>

              <h2 className="text-3xl font-semibold mb-4">Discounted Books</h2>
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                {discountedBooks.map(book => (
                  <BookCard
                    key={book._id}
                    book={book}
                    isWishlisted={wishlist.includes(book._id)}
                    onWishlistToggle={toggleWishlist}
                    onAddToCart={addToCart}
                  />
                ))}
              </div>
            </section>

            <section>
              <h2 className="text-3xl font-semibold mb-4">All Books</h2>
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                {filteredBooks.map(book => (
                  <BookCard
                    key={book._id}
                    book={book}
                    isWishlisted={wishlist.includes(book._id)}
                    onWishlistToggle={toggleWishlist}
                    onAddToCart={addToCart}
                  />
                ))}
              </div>
            </section>
          </>
        ) : (
          <p className="text-center text-lg text-gray-700">Please log in to view and add books to your wishlist or cart.</p>
        )}
      </main>
    </div>
  );
}

function BookCard({
  book,
  isWishlisted,
  onWishlistToggle,
  onAddToCart,
}: {
  book: Book;
  isWishlisted: boolean;
  onWishlistToggle: (id: string) => void;
  onAddToCart: (id: string) => void;
}) {
  return (
    <Card className="hover:shadow-lg transition-shadow duration-300">
      <Link to={`/books/${book._id}`} className="block">
        <CardHeader>
          <img src={book.images[0] || "/placeholder.svg"} alt={book.title} className="w-full h-48 object-cover rounded-md" />
        </CardHeader>
        <CardContent className="p-4">
          <h3 className="font-semibold text-xl">{book.title}</h3>
          <p className="text-sm text-gray-600">{book.author}</p>
          <div className="flex items-center mt-2">
            <Badge>{book.category}</Badge>
            {book.discount > 0 && <span className="ml-2 text-red-600 font-semibold">{book.discount}% OFF</span>}
          </div>
          <div className="flex justify-between items-center mt-4">
            <span className="font-semibold text-xl">${book.price}</span>
            <div className="flex items-center space-x-2">
              <Button
                variant="outline"
                onClick={() => onWishlistToggle(book._id)}
                className="p-2 rounded-md"
              >
                <Heart
                  color={isWishlisted ? "red" : "gray"}
                  size={20}
                />
              </Button>
              <Button
                variant="outline"
                onClick={() => onAddToCart(book._id)}
                className="p-2 rounded-md"
              >
                <ShoppingCart size={20} />
              </Button>
            </div>
          </div>
        </CardContent>
      </Link>
    </Card>
  );
}
