'use client'

import { useState, useEffect } from 'react'
import { Heart, ShoppingCart } from 'lucide-react'
import { useParams } from 'react-router-dom'
import { Button } from "./ui/button"
import { Card, CardContent, CardFooter, CardHeader, CardTitle } from "./ui/card"
import { Badge } from "./ui/badge"

type Book = {
  _id: string;
  title: string;
  author: string;
  description: string;
  price: number;
  discount: number;
  category: string;
  stock: number;
  images: string[];
}

export default function BookDetailsPage() {
  const { id } = useParams<{ id: string }>()
  const [book, setBook] = useState<Book | null>(null)
  const [isWishlisted, setIsWishlisted] = useState(false)

  useEffect(() => {
    fetchBook()
    checkWishlistStatus()
  }, [id])

  const fetchBook = async () => {
    try {
      const response = await fetch(`http://localhost:5000/api/books/${id}`)
      const data = await response.json()
      setBook(data)
    } catch (error) {
      console.error('Error fetching book:', error)
    }
  }

  const checkWishlistStatus = async () => {
    try {
      const response = await fetch(`http://localhost:5000/api/wishlist`)
      const data = await response.json()
      setIsWishlisted(data.books.some((book: { _id: string }) => book._id === id))
    } catch (error) {
      console.error('Error checking wishlist status:', error)
    }
  }

  const toggleWishlist = async () => {
    try {
      const url = isWishlisted
        ? `http://localhost:5000/api/wishlist/remove/${id}`
        : 'http://localhost:5000/api/wishlist/add'
      const method = isWishlisted ? 'DELETE' : 'POST'
      const body = isWishlisted ? null : JSON.stringify({ bookId: id })

      await fetch(url, {
        method,
        headers: {
          'Content-Type': 'application/json',
        },
        body,
      })
      setIsWishlisted(!isWishlisted)
    } catch (error) {
      console.error('Error updating wishlist:', error)
    }
  }

  const addToCart = async () => {
    try {
      await fetch('http://localhost:5000/api/carts/add', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ bookId: id, quantity: 1 }),
      })
      // You might want to show a success message or update a cart icon here
    } catch (error) {
      console.error('Error adding to cart:', error)
    }
  }

  if (!book) {
    return <div>Loading...</div>
  }

  return (
    <div className="container mx-auto px-4 py-8">
      <Card className="overflow-hidden">
        <CardHeader className="grid grid-cols-1 md:grid-cols-2 gap-6">
          <img
            src={book.images[0] || "/placeholder.svg"}
            alt={book.title}
            className="w-full h-96 object-cover rounded-lg"
          />
          <div className="space-y-4">
            <CardTitle className="text-3xl font-bold">{book.title}</CardTitle>
            <p className="text-xl text-gray-600">{book.author}</p>
            <Badge>{book.category}</Badge>
            <div className="flex items-center space-x-2">
              {book.discount > 0 ? (
                <>
                  <span className="text-2xl font-bold">${(book.price * (1 - book.discount / 100)).toFixed(2)}</span>
                  <span className="text-lg text-gray-500 line-through">${book.price.toFixed(2)}</span>
                </>
              ) : (
                <span className="text-2xl font-bold">${book.price.toFixed(2)}</span>
              )}
            </div>
            <p className="text-lg">Stock: {book.stock}</p>
          </div>
        </CardHeader>
        <CardContent className="mt-6">
          <h3 className="text-xl font-semibold mb-2">Description</h3>
          <p className="text-gray-700">{book.description}</p>
        </CardContent>
        <CardFooter className="flex justify-between mt-6">
          <Button onClick={addToCart} className="flex-1 mr-2">
            <ShoppingCart className="mr-2 h-4 w-4" /> Add to Cart
          </Button>
          <Button onClick={toggleWishlist} variant="outline" className="flex-1 ml-2">
            <Heart className={`mr-2 h-4 w-4 ${isWishlisted ? 'fill-red-500 text-red-500' : ''}`} />
            {isWishlisted ? 'Remove from Wishlist' : 'Add to Wishlist'}
          </Button>
        </CardFooter>
      </Card>
    </div>
  )
}
