'use client';

import { useState, useEffect } from 'react';
import { Tabs, TabsContent, TabsList, TabsTrigger } from "./ui/tabs";
import { Button } from "./ui/button";
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from "./ui/card";
import { Badge } from "./ui/badge";
import { Package, Heart, LogOut } from 'lucide-react';

// Define TypeScript interfaces for data types
interface Order {
  id: string;
  status: string;
  total: number;
}

interface WishlistItem {
  id: string;
  name: string;
}

export default function ProfilePage() {
  const [orders, setOrders] = useState<Order[]>([]);
  const [wishlist, setWishlist] = useState<WishlistItem[]>([]);
  const [loadingOrders, setLoadingOrders] = useState(true);
  const [loadingWishlist, setLoadingWishlist] = useState(true);

  const [fetchError, setFetchError] = useState<{ orders: string | null; wishlist: string | null }>({
    orders: null,
    wishlist: null,
  });

  const fetchOrders = async (token: string) => {
    try {
      const response = await fetch('http://localhost:5000/api/orders', {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
  
      if (!response.ok) {
        throw new Error(`Failed to fetch orders: ${response.status}`);
      }
  
      const data: Order[] = await response.json();
      setOrders(data);
    } catch (error) {
      const err = error as Error;
      setFetchError((prev) => ({ ...prev, orders: err.message || "An unknown error occurred." }));
    } finally {
      setLoadingOrders(false);
    }
  };
  
  const fetchWishlist = async (token: string) => {
    try {
      const response = await fetch('http://localhost:5000/api/wishlist', {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
  
      if (!response.ok) {
        throw new Error(`Failed to fetch wishlist: ${response.status}`);
      }
  
      const data: WishlistItem[] = await response.json();
      setWishlist(data);
    } catch (error) {
      const err = error as Error;
      setFetchError((prev) => ({ ...prev, wishlist: err.message || "An unknown error occurred." }));
    } finally {
      setLoadingWishlist(false);
    }
  };
  
  const handleLogout = () => {
    localStorage.removeItem('token');
    window.location.href = '/login'; // Redirect to login page
  };

  useEffect(() => {
    const token = typeof window !== "undefined" ? localStorage.getItem('token') : null;
    if (token) {
      fetchOrders(token);
      fetchWishlist(token);
    }
  }, []);

  return (
    <div className="container mx-auto px-4 py-8">
      <h1 className="text-3xl font-bold mb-6">My Profile</h1>
      <Tabs defaultValue="orders">
        <TabsList className="grid w-full grid-cols-3 mb-8">
          <TabsTrigger value="orders" className="flex items-center">
            <Package className="mr-2 h-4 w-4" />
            Orders
          </TabsTrigger>
          <TabsTrigger value="wishlist" className="flex items-center">
            <Heart className="mr-2 h-4 w-4" />
            Wishlist
          </TabsTrigger>
          <TabsTrigger value="logout" className="flex items-center">
            <LogOut className="mr-2 h-4 w-4" />
            Logout
          </TabsTrigger>
        </TabsList>
        <TabsContent value="orders">
          <Card>
            <CardHeader>
              <CardTitle>Your Orders</CardTitle>
              <CardDescription>View and manage your order history.</CardDescription>
            </CardHeader>
            <CardContent>
              {loadingOrders ? (
                <p>Loading orders...</p>
              ) : fetchError.orders ? (
                <p className="text-red-500">Error: {fetchError.orders}</p>
              ) : orders.length > 0 ? (
                <ul>
                  {orders.map((order) => (
                    <li key={order.id} className="mb-4">
                      <Badge>{order.status}</Badge>
                      <p>Order ID: {order.id}</p>
                      <p>Total: ${order.total}</p>
                    </li>
                  ))}
                </ul>
              ) : (
                <p>No orders found.</p>
              )}
            </CardContent>
          </Card>
        </TabsContent>
        <TabsContent value="wishlist">
          <Card>
            <CardHeader>
              <CardTitle>Your Wishlist</CardTitle>
              <CardDescription>Books you've saved for later.</CardDescription>
            </CardHeader>
            <CardContent>
              {loadingWishlist ? (
                <p>Loading wishlist...</p>
              ) : fetchError.wishlist ? (
                <p className="text-red-500">Error: {fetchError.wishlist}</p>
              ) : wishlist.length > 0 ? (
                <ul>
                  {wishlist.map((item) => (
                    <li key={item.id} className="mb-4">
                      <p>{item.name}</p>
                    </li>
                  ))}
                </ul>
              ) : (
                <p>Your wishlist is empty.</p>
              )}
            </CardContent>
          </Card>
        </TabsContent>
        <TabsContent value="logout">
          <Card>
            <CardHeader>
              <CardTitle>Logout</CardTitle>
              <CardDescription>Are you sure you want to log out?</CardDescription>
            </CardHeader>
            <CardContent>
              <Button onClick={handleLogout} variant="destructive">
                Confirm Logout
              </Button>
            </CardContent>
          </Card>
        </TabsContent>
      </Tabs>
    </div>
  );
}
