'use client';
import { useState, useEffect } from 'react';
import { Heart, Search, ShoppingCart } from 'lucide-react';
import { Link } from 'react-router-dom';
import { Button } from './ui/button';
import { Input } from './ui/input';
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from './ui/select';
import { Card, CardContent, CardFooter, CardHeader, CardTitle } from './ui/card';
import { Badge } from './ui/badge';
import Navbar from './Navbar';

type Book = {
  _id: string;
  title: string;
  author: string;
  price: number;
  discount: number;
  category: string;
  images: string[];
};

export default function HomePage() {
  const [books, setBooks] = useState<Book[]>([]);
  const [categories, setCategories] = useState<string[]>([]);
  const [authors, setAuthors] = useState<string[]>([]);
  const [selectedCategory, setSelectedCategory] = useState('All');
  const [selectedAuthor, setSelectedAuthor] = useState('All');
  const [wishlist, setWishlist] = useState<string[]>([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  useEffect(() => {
    fetchBooks();
    checkAuthStatus();
  }, []);

  useEffect(() => {
    if (isAuthenticated) {
      fetchWishlist();
    }
  }, [isAuthenticated]);

  const checkAuthStatus = () => {
    const token = localStorage.getItem('token');
    setIsAuthenticated(!!token);
  };

  const fetchBooks = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/books');
      const data: Book[] = await response.json();
      setBooks(data);

      const uniqueCategories = Array.from(
        new Set(data.map((book: Book) => book.category))
      );
      const uniqueAuthors = Array.from(
        new Set(data.map((book: Book) => book.author))
      );

      setCategories(['All', ...uniqueCategories]);
      setAuthors(['All', ...uniqueAuthors]);
    } catch (error) {
      console.error('Error fetching books:', error);
    }
  };

  const fetchWishlist = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:5000/api/wishlist', {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      if (response.status === 401) {
        setIsAuthenticated(false);
        return;
      }
      const data = await response.json();
      setWishlist(data.books.map((book: Book) => book._id));
    } catch (error) {
      console.error('Error fetching wishlist:', error);
    }
  };

  const toggleWishlist = async (bookId: string) => {
    if (!isAuthenticated) {
      alert('Please log in to add items to your wishlist');
      return;
    }

    try {
      const token = localStorage.getItem('token');
      if (wishlist.includes(bookId)) {
        await fetch(`http://localhost:5000/api/wishlist/remove/${bookId}`, {
          method: 'DELETE',
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setWishlist(wishlist.filter((id) => id !== bookId));
      } else {
        await fetch('http://localhost:5000/api/wishlist/add', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
            Authorization: `Bearer ${token}`,
          },
          body: JSON.stringify({ bookId }),
        });
        setWishlist([...wishlist, bookId]);
      }
    } catch (error) {
      console.error('Error updating wishlist:', error);
    }
  };

  const addToCart = async (bookId: string) => {
    if (!isAuthenticated) {
      alert('Please log in to add items to your cart');
      return;
    }

    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:5000/api/carts/add', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ bookId, quantity: 1 }),
      });

      if (response.ok) {
        alert('Book added to cart successfully');
      } else {
        console.error('Failed to add book to cart');
        alert('Failed to add book to cart');
      }
    } catch (error) {
      console.error('Error adding to cart:', error);
      alert('Error adding to cart');
    }
  };

  const filteredBooks = books.filter(
    (book) =>
      (selectedCategory === 'All' || book.category === selectedCategory) &&
      (selectedAuthor === 'All' || book.author === selectedAuthor) &&
      (book.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
        book.author.toLowerCase().includes(searchTerm.toLowerCase()))
  );

  return (
    <div className="min-h-screen bg-gray-100">
      <Navbar />
      <main className="container mx-auto px-4 py-8">
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {filteredBooks.map((book) => (
            <BookCard
              key={book._id}
              book={book}
              isWishlisted={wishlist.includes(book._id)}
              onWishlistToggle={toggleWishlist}
              onAddToCart={addToCart}
            />
          ))}
        </div>
      </main>
    </div>
  );
}

function BookCard({
  book,
  isWishlisted,
  onWishlistToggle,
  onAddToCart,
}: {
  book: Book;
  isWishlisted: boolean;
  onWishlistToggle: (id: string) => void;
  onAddToCart: (id: string) => void;
}) {
  return (
    <Card>
      <Link to={`/books/${book._id}`} className="block">
        <CardHeader>
          <img src={book.images[0] || '/placeholder.svg'} alt={book.title} className="w-full h-48 object-cover" />
        </CardHeader>
        <CardContent>
          <h3 className="font-semibold">{book.title}</h3>
          <p className="text-sm text-gray-600">{book.author}</p>
        </CardContent>
      </Link>
      <CardFooter className="flex justify-between items-center">
        <Button variant="outline" size="sm" onClick={() => onWishlistToggle(book._id)}>
          <Heart size={18} className={isWishlisted ? 'text-red-500' : 'text-gray-400'} />
        </Button>
        <Button variant="default" size="sm" onClick={() => onAddToCart(book._id)}>
          <ShoppingCart size={18} />
        </Button>
      </CardFooter>
    </Card>
  );
}
