import React, { useEffect, useState } from "react";
import { Badge } from "../components/ui/badge";
import { Button } from "../components/ui/button";
import { Card, CardHeader, CardTitle, CardDescription, CardContent, CardFooter } from ".../components/ui/card";
import { Tabs, TabsList, TabsTrigger, TabsContent } from "../components/ui/tabs";
import { Input } from "../components/ui/input";
import { Select, SelectTrigger, SelectContent, SelectItem } from "./components/ui/select";

const Homepage = () => {
  const [cars, setCars] = useState([]);
  const [bookings, setBookings] = useState([]);
  const [user, setUser] = useState(null);
  const [selectedCar, setSelectedCar] = useState(null);

  // Fetch data from the backend
  useEffect(() => {
    fetch("/api/cars")
      .then((res) => res.json())
      .then(setCars);

    fetch("/api/bookings")
      .then((res) => res.json())
      .then(setBookings);

    fetch("/api/user")
      .then((res) => res.json())
      .then(setUser);
  }, []);

  const handleBooking = (carId) => {
    fetch("/api/bookings", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        carId,
        userId: user.id,
        bookingDate: new Date(),
      }),
    })
      .then((res) => res.json())
      .then((newBooking) => setBookings((prev) => [...prev, newBooking]));
  };

  return (
    <div className="p-6">
      <Tabs defaultValue="cars">
        <TabsList>
          <TabsTrigger value="cars">Available Cars</TabsTrigger>
          <TabsTrigger value="bookings">Your Bookings</TabsTrigger>
        </TabsList>

        <TabsContent value="cars">
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
            {cars.map((car) => (
              <Card key={car.id}>
                <CardHeader>
                  <CardTitle>{car.make} {car.model}</CardTitle>
                  <CardDescription>{car.year} | {car.type}</CardDescription>
                </CardHeader>
                <CardContent>
                  <p>Price per day: ${car.pricePerDay}</p>
                </CardContent>
                <CardFooter>
                  <Button onClick={() => handleBooking(car.id)}>Book Now</Button>
                </CardFooter>
              </Card>
            ))}
          </div>
        </TabsContent>

        <TabsContent value="bookings">
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
            {bookings.map((booking) => (
              <Card key={booking.id}>
                <CardHeader>
                  <CardTitle>{booking.car.make} {booking.car.model}</CardTitle>
                  <CardDescription>Booked on: {new Date(booking.bookingDate).toLocaleDateString()}</CardDescription>
                </CardHeader>
                <CardContent>
                  <Badge variant="secondary">Booking ID: {booking.id}</Badge>
                </CardContent>
              </Card>
            ))}
          </div>
        </TabsContent>
      </Tabs>
    </div>
  );
};

export default Homepage;
