'use client'
import { useState, useEffect } from 'react'
import { Heart, Search, ShoppingCart, User } from 'lucide-react'
import { Button } from "./ui/button"
import { Input } from "./ui/input"
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "./ui/select"
import { Card, CardContent, CardFooter, CardHeader, CardTitle } from "./ui/card"
import { Badge } from "./ui/badge"

type Book = {
    _id: string;
    title: string;
    author: string;
    price: number;
    discount: number;
    category: string;
    images: string[];
  }

export default function Component() {
  const [books, setBooks] = useState<Book[]>([]);
  const [categories, setCategories] = useState<string[]>([]);
  const [authors, setAuthors] = useState<string[]>([]);
  const [selectedCategory, setSelectedCategory] = useState("All");
  const [selectedAuthor, setSelectedAuthor] = useState("All");
  const [wishlist, setWishlist] = useState<string[]>([]);
  const [searchTerm, setSearchTerm] = useState("");

  useEffect(() => {
    fetchBooks();
    fetchCategories();
    fetchAuthors();
    fetchWishlist();
  }, []);

  const fetchBooks = async () => {
    try {
      const response = await fetch('/api/books');
      const text = await response.text(); // Read the response as text first
      console.log('Books response:', text); // Log the raw response for debugging
      const data = JSON.parse(text); // Then parse it into JSON
      setBooks(data);
    } catch (error) {
      console.error('Error fetching books:', error);
    }
  };

  const fetchCategories = async () => {
    try {
      const response = await fetch('/api/books/categories');
      const data = await response.json();
      setCategories(['All', ...data]);
    } catch (error) {
      console.error('Error fetching categories:', error);
    }
  };

  const fetchAuthors = async () => {
    try {
      const response = await fetch('/api/books/authors');
      const data = await response.json();
      setAuthors(['All', ...data]);
    } catch (error) {
      console.error('Error fetching authors:', error);
    }
  };

  const fetchWishlist = async () => {
    try {
      const response = await fetch('/api/wishlist');
      const data = await response.json();
      setWishlist(data.books.map((book: Book) => book._id));
    } catch (error) {
      console.error('Error fetching wishlist:', error);
    }
  };

  const toggleWishlist = async (bookId: string) => {
    try {
      if (wishlist.includes(bookId)) {
        await fetch(`/api/wishlist/remove/${bookId}`, { method: 'DELETE' });
        setWishlist(wishlist.filter(id => id !== bookId));
      } else {
        await fetch('/api/wishlist/add', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ bookId })
        });
        setWishlist([...wishlist, bookId]);
      }
    } catch (error) {
      console.error('Error updating wishlist:', error);
    }
  };

  const addToCart = async (bookId: string) => {
    try {
      await fetch('/api/carts/add', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ bookId, quantity: 1 })
      });
      // You might want to show a success message or update a cart icon here
    } catch (error) {
      console.error('Error adding to cart:', error);
    }
  };

  const filteredBooks = books.filter(book => 
    (selectedCategory === "All" || book.category === selectedCategory) &&
    (selectedAuthor === "All" || book.author === selectedAuthor) &&
    (book.title.toLowerCase().includes(searchTerm.toLowerCase()) || 
     book.author.toLowerCase().includes(searchTerm.toLowerCase()))
  );

  const discountedBooks = filteredBooks.filter(book => book.discount > 0);

  return (
    <div className="min-h-screen bg-gray-100">
      <header className="bg-white shadow">
        <div className="container mx-auto px-4 py-6 flex items-center justify-between">
          <h1 className="text-2xl font-bold">BookStore</h1>
          <div className="flex items-center space-x-4">
            <div className="relative">
              <Search className="absolute left-2 top-1/2 transform -translate-y-1/2 text-gray-400" />
              <Input 
                className="pl-8" 
                placeholder="Search books..." 
                value={searchTerm}
                onChange={(e: React.ChangeEvent<HTMLInputElement>) => setSearchTerm(e.target.value)}
              />
            </div>
            <Button variant="ghost" size="icon">
              <ShoppingCart className="h-5 w-5" />
            </Button>
            <Button variant="ghost" size="icon">
              <User className="h-5 w-5" />
            </Button>
          </div>
        </div>
      </header>

      <main className="container mx-auto px-4 py-8">
        <div className="grid grid-cols-4 gap-8">
          <aside className="col-span-1">
            <Card>
              <CardHeader>
                <CardTitle>Filters</CardTitle>
              </CardHeader>
              <CardContent className="space-y-4">
                <div>
                  <label className="text-sm font-medium">Category</label>
                  <Select value={selectedCategory} onValueChange={setSelectedCategory}>
                    <SelectTrigger>
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      {categories.map(category => (
                        <SelectItem key={category} value={category}>{category}</SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                </div>
                <div>
                  <label className="text-sm font-medium">Author</label>
                  <Select value={selectedAuthor} onValueChange={setSelectedAuthor}>
                    <SelectTrigger>
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      {authors.map(author => (
                        <SelectItem key={author} value={author}>{author}</SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                </div>
              </CardContent>
            </Card>
          </aside>

          <div className="col-span-3 space-y-8">
            <section>
              <h2 className="text-2xl font-semibold mb-4">Discounted Books</h2>
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                {discountedBooks.map(book => (
                  <BookCard key={book._id} book={book} isWishlisted={wishlist.includes(book._id)} onWishlistToggle={toggleWishlist} onAddToCart={addToCart} />
                ))}
              </div>
            </section>

            <section>
              <h2 className="text-2xl font-semibold mb-4">All Books</h2>
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                {filteredBooks.map(book => (
                  <BookCard key={book._id} book={book} isWishlisted={wishlist.includes(book._id)} onWishlistToggle={toggleWishlist} onAddToCart={addToCart} />
                ))}
              </div>
            </section>
          </div>
        </div>
      </main>
    </div>
  )
}

function BookCard({ book, isWishlisted, onWishlistToggle, onAddToCart }: {
    book: Book;
    isWishlisted: boolean;
    onWishlistToggle: (id: string) => void;
    onAddToCart: (id: string) => void;
  }){
  return (
    <Card>
      <CardHeader>
        <img src={book.images[0] || "/placeholder.svg"} alt={book.title} className="w-full h-48 object-cover" />
      </CardHeader>
      <CardContent>
        <h3 className="font-semibold">{book.title}</h3>
        <p className="text-sm text-gray-600">{book.author}</p>
        <div className="flex items-center justify-between mt-2">
          {book.discount > 0 ? (
            <div>
              <span className="text-lg font-bold">${(book.price * (1 - book.discount / 100)).toFixed(2)}</span>
              <span className="text-sm text-gray-500 line-through ml-2">${book.price.toFixed(2)}</span>
            </div>
          ) : (
            <span className="text-lg font-bold">${book.price.toFixed(2)}</span>
          )}
          <Badge>{book.category}</Badge>
        </div>
      </CardContent>
      <CardFooter className="flex justify-between">
        <Button onClick={() => onAddToCart(book._id)}>Add to Cart</Button>
        <Button variant="ghost" size="icon" onClick={() => onWishlistToggle(book._id)}>
          <Heart className={`h-5 w-5 ${isWishlisted ? 'fill-red-500 text-red-500' : 'text-gray-500'}`} />
        </Button>
      </CardFooter>
    </Card>
  )
}
