'use client'

import { useState, useEffect } from 'react'
import { Trash2 } from 'lucide-react'
import { Button } from "./ui/button"
import { Link } from 'react-router-dom'
import { Card, CardContent, CardFooter, CardHeader, CardTitle } from "./ui/card"
import Navbar from './Navbar'

type Car = {
  _id: string
  name: string
  model: string
  pricePerDay: number
}

type CartItem = {
  car: Car
  startDate: string
  endDate: string
  days: number
  pricePerDay: number
}

type Cart = {
  _id: string
  user: string
  items: CartItem[]
  totalAmount: number
}

export default function CartPage() {
  const [cart, setCart] = useState<Cart | null>(null)
  const [isLoading, setIsLoading] = useState(true)

  useEffect(() => {
    fetchCart()
  }, [])

  const fetchCart = async () => {
    setIsLoading(true)
    try {
      const token = localStorage.getItem('token')
      if (!token) {
        console.error('No token found, redirecting to login')
        return
      }
      const response = await fetch('http://localhost:5000/api/cart', {
        headers: { 'Authorization': `Bearer ${token}` }
      })
      if (response.ok) {
        const cartData = await response.json()
        setCart(cartData)
      } else {
        console.error('Failed to fetch cart, status:', response.status)
      }
    } catch (error) {
      console.error('Error fetching cart:', error)
    } finally {
      setIsLoading(false)
    }
  }

  const removeFromCart = async (carId: string) => {
    try {
      const token = localStorage.getItem('token')
      const response = await fetch('http://localhost:5000/api/cart', {
        method: 'DELETE',
        headers: { 'Authorization': `Bearer ${token}`, 'Content-Type': 'application/json' },
        body: JSON.stringify({ carId })
      })
      if (response.ok) {
        fetchCart() // Refresh the cart after removal
      } else {
        console.error('Failed to remove item from cart, status:', response.status)
      }
    } catch (error) {
      console.error('Error removing item from cart:', error)
    }
  }

  if (isLoading) {
    return <div className="flex justify-center items-center h-screen">Loading...</div>
  }

  return (
    <div className="min-h-screen bg-gray-100">
      <Navbar />
      <main className="container mx-auto px-4 py-8">
        <h1 className="text-3xl font-bold mb-8">Your Rental Cart</h1>
        {cart && cart.items && cart.items.length > 0 ? (
          <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
            <div className="md:col-span-2 space-y-4">
              {cart.items.map((item) => (
                <Card key={item.car._id}>
                  <CardContent className="p-6 flex items-center space-x-4">
                    <div className="flex-grow">
                      <h3 className="font-semibold">{item.car.name}</h3>
                      <p className="text-sm text-gray-600">{item.car.model}</p>
                      <p className="text-lg font-bold mt-2">
                        ${item.pricePerDay.toFixed(2)} per day
                      </p>
                      <p className="text-sm text-gray-600">
                        From: {new Date(item.startDate).toLocaleDateString()} - 
                        To: {new Date(item.endDate).toLocaleDateString()}
                      </p>
                      <p className="text-sm text-gray-600">
                        Duration: {item.days} days
                      </p>
                    </div>
                    <Button
                      variant="ghost"
                      size="icon"
                      onClick={() => removeFromCart(item.car._id)}
                    >
                      <Trash2 className="h-5 w-5 text-red-500" />
                    </Button>
                  </CardContent>
                </Card>
              ))}
            </div>
            <div className="md:col-span-1">
              <Card>
                <CardHeader>
                  <CardTitle>Rental Summary</CardTitle>
                </CardHeader>
                <CardContent>
                  <div className="space-y-2">
                    <div className="flex justify-between font-bold text-lg">
                      <span>Total</span>
                      <span>${cart.totalAmount.toFixed(2)}</span>
                    </div>
                  </div>
                </CardContent>
                <CardFooter>
                  <Link to="/checkout">
                    <Button className="w-full">Proceed to Checkout</Button>
                  </Link>
                </CardFooter>
              </Card>
            </div>
          </div>
        ) : (
          <div className="text-center">
            <p className="text-xl mb-4">Your rental cart is empty</p>
            <Button asChild>
              <Link to="/cars">Browse Available Cars</Link>
            </Button>
          </div>
        )}
      </main>
    </div>
  )
}
